// ==UserScript==
// @name       Custom Exports (W09, V11, and V13)
// @version    3.0beta
// @description  Enables custom SQL exports in the Volusion admin area (now including V13!)
// @include        /admin/
// @require        https://ajax.googleapis.com/ajax/libs/jquery/1.4.2/jquery.min.js
// ==/UserScript==

/* Template file may be found here: 
https://lead-mueller.com/v/sam/sampleQT.js   

sample files:
- https://lead-mueller.com/v/sam/CSR3/queries.js
- https://lead-mueller.com/v/sam/CSR3/quickbooks.js
*/

/* CONFIGS */
var dynamicQueriesEnabled = true;
var userfile = "https://sam-ess.com/v/reports/saved_reports/sam.js";
/* end user editable CONFIGS*/

var loadingGif = "data:image/gif;base64,";

var stdyn = 'st';
var rprts = [];
var file_exists = 0;
var file_err = false;
var err_status;
var sw = jQuery.noConflict();
var CustomSQLExport = {
    // DOM function to obtain store version
    GetStoreVersion: function () {
        if (sw("img[src*=/v13/i/volusion_logo.png]").length) {
            return 'V13';
        } else if (sw("img[src*=/65/i/volusion_logo.png]").length) {
            return 'V11';
        } else if (typeof unsafeWindow.adm_storeversion != 'undefined') {
            if (unsafeWindow.adm_storeversion.indexOf("6.0") > -1) {
                return 'W09';
            }
        } else {
            return 'Other';
        }
    },

    // DOM function to add nav menu (duh)
    AddNavMenu: function () {
        var CurVersion = this.GetStoreVersion();
        if (CurVersion === 'V13' || CurVersion === 'V11') {
            sw('<li class="subnav" id="Inventory_SubNav_CustomSQL" unselectable="on"><a id="CustomSQLLink" href="TableViewer.asp?table=polls" title="">SQL Export</a></li>').insertAfter("#Inventory_SubNav_Products");
            sw("#CustomSQLLink").hover(function () {
                sw(".subnav").removeClass('subnav hover').addClass('subnav');
                sw("#Inventory_SubNav_CustomSQL").removeClass('subnav').addClass('subnav hover');
            });
        } else if (CurVersion === 'W09') {
            sw('<li><a rev="CustomSQLExport" href="TableViewer.asp?table=polls">SQL</a></li>').insertAfter(sw("a[rev=Products]").parent());
        }
    },

    // DOM function to apply GUI
    ApplyStyling: function () {
        if (stdyn === 'st') { // static reports
            console.log("Static GUI initializing...");
            sw(".CustomSQLArea").html('<table id="statustbl" style="float:left; border:none;"><tbody><tr><td style="padding:10px;position:relative"><span style="font-weight:bold">Status:</span> <span id="statuslbl">Ready!</span></td></tr></tbody></table></td></tr></table><br><table id="exporthistory" style="float:right; margin-right:10%; position:relative; width:30%;" border="1px solid black"><tr><th>SQL</th><th>D/L Link</th></tr><tr><td><textarea width="100%" style="position:relative; right:2px;" class="historicalsql"></textarea></td><td class="downloadlink"><a href="javascript:void(0);">No File Yet</a></td></tr></table><table style="float:left; width:50%;"><tr><td><textarea autofocus="autofocus" style="width:100%;float:left;margin-left:10px;" rows="6" id="CustomSQLField">SELECT ProductCode FROM Products_Joined</textarea><br><br><button type="button" id="sqlbutton" style="float:left; width:30%; height:30px; margin-left:2%;margin-bottom:10px;">Export Report</button><select id="SavedQueries" style="float:right;"><option>Quick Queries</option><option>------</option><option value="1">Admin History</option><option value="2">Check Bulk Updates</option><option value="3">Payment Logs by Keyword</option><option value="4">All Tables and Fields</option><option value="5">TIMEOUT Orders</option><option value="6">Raw Payment Log for Order</option></select>');
        }
        if (stdyn === 'dy') { //dynamic reports
            console.log("Dynamic GUI initializing...");
            if (rprts !== [] && rprts !== undefined && rprts !== 'undefined') {
                // add that funky loop here
                var html = '<table id="statustbl" style="float:left; border:none;"><tbody><tr><td style="padding:10px;position:relative"><span style="font-weight:bold">Status:</span> <span id="statuslbl">Ready!</span></td></tr></tbody></table></td></tr></table><br><table id="exporthistory" style="float:right; margin-right:10%; position:relative; width:30%;" border="1px solid black"><tr><th>SQL</th><th>D/L Link</th></tr><tr><td><textarea width="100%" style="position:relative; right:2px;" class="historicalsql"></textarea></td><td class="downloadlink"><a href="javascript:void(0);">No File Yet</a></td></tr></table><table style="float:left; width:50%;"><tr><td><textarea autofocus="autofocus" style="width:100%;float:left;margin-left:10px;" rows="6" id="CustomSQLField">SELECT ProductCode FROM Products_Joined</textarea><br><br><button type="button" id="sqlbutton" style="float:left; width:30%; height:30px; margin-left:2%;margin-bottom:10px;">Export Report</button>';
                var tempdat;
                var options = '<select id="SavedQueries" style="float:right;"><option>Quick Queries</option><option>------</option>';
                sw.each(rprts, function (key, value) {
                    tempdat = '<option value="' + key + '">' + value.title + '</option>';
                    options += tempdat;
                });
                options += '</select>';
                html += options;
                sw(".CustomSQLArea").html(html);
                CustomSQLExport.applyListeners();
            } else { // throw error that reports not initiliazed; initiate static reports
                console.error("Failed to initialize User Reports");
                CustomSQLExport.SetStatusLabel('Dynamic reports failed to initialize; Static reports initializing.', 'E80000');
                stdyn = 'st';
                CustomSQLExport.ApplyStyling();
                CustomSQLExport.applyListeners();
            }
        }

        if (file_err) {
            CustomSQLExport.SetStatusLabel(err_status, 'E80000');
        }
    },


    // submits SQL data via ajax to export engine
    SendAjax: function () {
        var FormToken = sw("#NOSAVE___Form_Submission_Token").val();
        var CustomSQL = sw("#CustomSQLField").val();
        var update = false;
        if(CustomSQL.indexOf("update") > -1 || CustomSQL.indexOf("UPDATE") > -1 || CustomSQL.indexOf("Update") > -1 ){ update = true;}
        console.log(CustomSQL);
        sw.ajax({
            url: '/admin/db_export.asp',
            type: 'POST',
            data: 'NOSAVE___Form_Submission_Token=' + FormToken + '&FileType=CSV&CustomExportSQL=' + encodeURIComponent(CustomSQL) + '&Submit=Upload',

            // AJAX returns data under a success call
            success: function (data) {
                // export engine returned report of invalid SQL
                if (data.indexOf("Your SQL is invalid") > -1) {
                    var $tempdata = sw(data);
                    var SyntaxReason = $tempdata.filter("span").text();
                    CustomSQLExport.SetStatusLabel(SyntaxReason, 'E80000');
                    return false;

                    // query run successfully, but no data was located 
                } else if (data.indexOf("There were no records returned for your export") > -1 || data.indexOf("There were no results returned for your export.") > -1 || data.indexOf("Your CUSTOM_EXPORT table is empty") > -1) {
                    CustomSQLExport.SetStatusLabel('No Results matched your query', 'FC6003');
                    return false;

                    // export not available in demo store
                } else if (data.indexOf("unavailable in the Free Trial") > -1) {
                    CustomSQLExport.SetStatusLabel('Demos are not supported due to security restrictions', 'E80000');
                    return false;
                } else if (data.indexOf("Please double-check that the characters you entered are normal A-Z or 0-9 alphanumeric letters or numbers") > -1) {
                    CustomSQLExport.SetStatusLabel('ERROR: Form passed invalid argument. Reload page.<div style="padding: 15px 0 5px 55px; font-size: 12px;">If error persists, switch to static queries or reinstall script</div>', 'E80000');
                    return false;
                }

                // system error: export engine returns data, but download link not present
                var DownloadURL = location.protocol + '//' + document.domain + sw(data).find("a[href*=delivery.asp]").attr('href');
                if (DownloadURL.indexOf('undefined') > -1) {
                    if(update){
                        CustomSQLExport.SetStatusLabel('Update posted!', '398A22');
                    }else {
                        CustomSQLExport.SetStatusLabel('Error: Unhandled response received. Please reload script.<div style="padding: 15px 0 5px 55px; font-size: 12px;">Response received, however export file not found. See Net Panel for debugging info.</div>', 'E80000');
                        console.log(data);
                        return false;
                    }
                }

                // query run successfully, data returned; create download link
                var SavedData = sw("#CustomSQLField").val();
                if (sw(".historicalsql").val().length === 0) {
                    console.log("history not found");
                    if(update){
                        sw(".historicalsql").text(SavedData);
                        sw(".downloadlink").html('Updated!');
                    }else{
                        sw(".historicalsql").text(SavedData);
                        sw(".downloadlink").html('<a href="' + DownloadURL + '">Download Now</a>');   
                    }
                } else {
                    console.log("history found");
                    if(update){
                        var CellToWrite = '<tr><td><textarea width="100%" style="position:relative; right:2px;">' + SavedData + '</textarea></td><td class="downloadlink">Updated!</td></tr>';
                        sw(CellToWrite).appendTo("#exporthistory");
                    }else{
                        var CellToWrite = '<tr><td><textarea width="100%" style="position:relative; right:2px;">' + SavedData + '</textarea></td><td class="downloadlink"><a href="' + DownloadURL + '">Download Now</a></td></tr>';
                        sw(CellToWrite).appendTo("#exporthistory");
                    }
                }

                // set success message
                sw("#CustomSQLField").val('');
                CustomSQLExport.SetStatusLabel('Done!', '398A22');
            },

            // AJAX call returns an error: 
            error: function (jqXHR, textStatus, errorThrown) {
                CustomSQLExport.SetStatusLabel('Fatal Error transmitting request to server.<div style="padding: 15px 0 5px 55px; font-size: 12px;">Server returned an unsuccessful response code. Resource blocked or cannot be found at current domain.</div>', 'E80000');
                console.log('Error Details below:');
                console.log(jqXHR);
                console.log(textStatus);
                console.log(errorThrown);
            }
        });
    },


    /* 3.0 dynamic addition  */
    initReports: function () {
        console.log("Dynamic queries AJAX initilializing...");
        // checks if file exists
        sw.ajax({
            url: userfile,
            type: 'HEAD',
            datatype: 'jsonp',
            jsonpCallback: "reports",
            timeout: 5000,
            error: function (error) {
                file_exists = 0;
            },
            success: function () {
                file_exists = 1;
            },
            complete: function () {
                if (file_exists === 1) {
                    CustomSQLExport.GetReports();
                } else {
                    console.error("Query File Not Found! \n" + userfile);
                    stdyn = 'st';
                    file_err = true;
                    err_status = 'Error accessing saved queries file: <div style="padding: 15px 0 5px 55px; font-size: 12px;">Server returned unsuccessful response code. Ensure file location is correct.</div>';
                    CustomSQLExport.init();
                }
            }
        });
    },
    // pulls in user generated saved queries file
    GetReports: function () {
        // loads query file

        sw.ajax({
            type: "get",
            url: userfile,
            dataType: "jsonp",
            jsonpCallback: "reports",
            success: function (data) {
                rprts = data.queries;
                CustomSQLExport.ValidateFile(); /*3.0 updated to initialize after reports pulled*/
            }
        });
    },

    ValidateFile: function () {
        if (rprts == []) {
            stdyn = 'st';
            file_err = true;
            err_status = 'Dynamic user file missing queries: Static queries loaded.';
            CustomSQLExport.init();
        } else {
            var errored = false;
            sw.each(rprts, function (key, value) {
                if (!value.title || !value.query) {
                    errored = true;
                    return false;
                }
            });
            if (errored) {
                stdyn = 'st';
                file_err = true;
                err_status = 'Incorrect syntax detected in dynamic user file: Static queries loaded.';
                CustomSQLExport.init();
                return false;
            }
            /*send forth thine data*/
            CustomSQLExport.ApplyStyling();
        }
    },

    // sets status label and corresponding color
    SetStatusLabel: function (Contents, ColorHex) {
        sw("#statuslbl")
            .html(Contents)
            .css('color', '#' + ColorHex);
    },

    /*   2.0 static saved queries  */
    //loads up saved queries
    SavedQuery: function (Index) {
        switch (parseFloat(Index)) {
            case 1:
                return "SELECT c.EmailAddress, c.FirstName, c.LastName, URLs.URL, AH.LastModified, ah.ah_ipaddress\nFROM AdminHistory AH \nINNER JOIN AdminHistory_URLs URLs ON URLs.ID_AdminHistory_URLs = AH.ID_AdminHistory_URLs\nINNER JOIN Customers c ON c.CustomerID = AH.AH_CustomerID\nWHERE AH.LastModified BETWEEN '2009-4-1' AND '2009-06-03'\nORDER BY AH.LastModified ASC";
            case 2:
                return "select ah.ah_id, el.ah_bulkupdate, ah.ah_customerid, ah.ah_ipaddress, ah.LastModified from adminhistory_editlog el\nleft join adminhistory ah on ah.ah_id = el.ah_id\nwhere ah_bulkupdate is not null\norder by ah.ah_id desc";
            case 3:
                return "select p.pay_orderid\n,r.pay_id\n,r.reply\n,P.pay_AuthDate\nfrom Payment_Log_Reply_Raw r \ninner join payment_log p on r.pay_id = p.pay_id\nInner join orders o on p.pay_orderid = o.orderid\nwhere r.reply like '%error text here%'\norder by p.pay_orderid desc";
            case 4:
                return "select column_name,* from information_schema.columns";
            case 5:
                return "SELECT p.Pay_OrderID\n,p.Pay_Result\n,o.CustomerID\n,P.pay_AuthDate\nfrom payment_log p\ninner join orders o on p.pay_orderid = o.orderid\nwhere p.pay_result = 'TIMEOUT'\norder by p.Pay_orderid desc";
            case 6:
                return "SELECT P.Pay_OrderID\n,R.Reply\n,P.pay_AuthDate\nfrom Payment_Log P\nInner Join Payment_Log_Reply_Raw R on p.Pay_ID = R.Pay_ID\nwhere p.pay_id in(select pay_id from payment_log where pay_orderid = 5)";
        }
    },
    applyListeners: function () {
        // saved queries featurette
        sw("#SavedQueries").change(function () {
            console.log("Change detected");
            var SQLCurVal = sw(this).val();
            if (stdyn === 'dy') {
                console.log("yes?");
                if (SQLCurVal != "Quick Queries" && SQLCurVal != "------") {
                    console.log("sqlVal: " + SQLCurVal + "\nquery: " + rprts[sw(this).val()].query);
                    sw("#CustomSQLField").val(rprts[sw(this).val()].query);
                }
            }
            if (stdyn === 'st') {
                if (SQLCurVal != "Quick Queries" && SQLCurVal != "------") {
                    console.log("static query used");
                    sw("#CustomSQLField").val(CustomSQLExport.SavedQuery(sw(this).val()));
                }
            }
            // sw(this).val('Quick Queries');
        });

        // adds functionality to submit button (generates loading gif while awaiting ajax response)
        sw("#sqlbutton").click(function () {
            CustomSQLExport.SendAjax();
            CustomSQLExport.SetStatusLabel('<img src="' + loadingGif + '" />');
        });
    },

    init: function () {
        if (stdyn === 'dy') {
            console.log("dynamic queries initiliazing...");
            CustomSQLExport.initReports();
        }
        if (stdyn === 'st') {
            console.log("static queries initiliazing...");
            CustomSQLExport.ApplyStyling();
        }
    }
};

// actually adds menu item to admin bar
CustomSQLExport.AddNavMenu();
if (location.href.toLowerCase().indexOf("table=polls") > -1 && CustomSQLExport.GetStoreVersion() != 'Other') {
    console.log(CustomSQLExport.GetStoreVersion());
    sw(".headertext").hide();
    sw("#tableviewnavwrapper").hide();
    sw("#print_pagename").text('Custom SQL Export');
    if (CustomSQLExport.GetStoreVersion() === 'W09') {
        sw("#pagelabel").css('margin-bottom', '10px');
    }
    sw("#tableviewer tbody tr td").addClass('CustomSQLArea');
    sw(".CustomSQLArea").html('<img style="padding:100px;" src="' + loadingGif + '"/>');
    if (dynamicQueriesEnabled) {
        stdyn = 'dy';
    } else {
        stdyn = 'st';
    }
    CustomSQLExport.init();
    console.log('Custom SQL Export Initialized');
}